package week2.day1;

public class LearnJumpStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 10;

		for (i = 0; i <= 10; i++) {
			if (i == 3) {
				continue;
			}
			System.out.print(i+ " ");
			if (i == 7) {
				break;
			}
			
			

		}
	
	}

}
